$('#checkallnumber').click(function(){

	var e = $('[name="numbers"]');

	var str = "";

	if($(this).is(':checked') == 1){

		for(var i = 0 ; i < e.length ; i++){
			e[i].checked = 1;
		}

		for(var i = 0 ; i < e.length ; i++){
			if(e[i].checked == 1){
				if(e[i].value != ""){
					str = str + e[i].value + ";";
				}
			}
		}

		$('#allnumbers').val(str);

	}else{
		for(var i = 0 ; i < e.length ; i++){
			e[i].checked = 0;
			$('#allnumbers').val('');
		}
	}

});

function passNumber(){

	var e = $('[name="numbers"]');

	var str = "";

	for(var i = 0 ; i < e.length ; i++){
		if(e[i].checked == 1){
			if(e[i].value != ""){
				str = str + e[i].value + ";";
			}
		}
	}

	$('#allnumbers').val(str);
}	


