function getDate(){

	var d = new Date();

	var h = "";
    var m = "";
    var s = "";

    if(d.getHours() < 10){
    	h = "0"+d.getHours();
    }else{
    	if(d.getHours() > 12){
    		h = "0" + (Number(d.getHours() - 12));
    	}else{
    		h = d.getHours();
    	}
	}

    if(d.getMinutes() < 10){
    	m = "0"+d.getMinutes();
    }else{
    	m = d.getMinutes();
    }

    if(d.getSeconds() < 10){
    	s = "0"+d.getSeconds();
    }else{
    	s = d.getSeconds();
    }

    if(d.getHours() >= 12){
    	time = h+":"+m+" PM";
    }else{
    	time = h+":"+m+" AM";
    }

    var year = d.getFullYear();
    var month = d.getMonth();
    month += 1;
    var day = d.getDate();

    if(month < 10){
    	month = "0" + month;
    }

    if(day < 10){
    	day = "0" + day;
    }

    $('#newreportdate').val(year+"-"+month+"-"+day);

    $('#newreporttime').val(time);

}
