function getColor(d) {
    return d > 1000 ? '#ff0000' :
           d > 500  ? '#ff4000' :
           d > 200  ? '#ff8000' :
           d > 100  ? '#ffbf00' :
           d > 50   ? '#ffff00' :
           d > 20   ? '#bfff00' :
           d > 0    ? '#80ff00' :
                      '#FFEFD5' ;
}

function style(feature) {
    return {
        fillColor: getColor(feature.properties.density),
        weight: 2,
        opacity: 1,
        color: 'white',
        dashArray: '3',
        fillOpacity: 0.7
    };
}

