function addCommaMap(item){
	if(isnull(item) == '-'){
		return item;
	}else{
		item = Number(item);
		return item.toLocaleString();
	}
}

function family(item){

	if(item <= 1){
		return 'family'; 
	}else{
		return 'families'
	}


}

